 /**
 * Generate an image URL from Pollinations API.
 *
 * @param prompt - The text prompt for the image
 * @param model - Optional style/model (default: cinematic-realism)
 * @returns The image URL as a string
 */
export function generateImageUrl(prompt: string, model?: string): string;

/**
 * Fetch the generated image as a Buffer (Node.js only).
 *
 * @param prompt - The text prompt
 * @param model - Optional style/model
 * @returns A Promise that resolves to an image Buffer
 */
export function fetchImageBuffer(prompt: string, model?: string): Promise<Buffer>;
