 /**
 * React hook to generate an AI image from Pollinations API.
 *
 * @param prompt - The text prompt for the image
 * @param model - Optional style/model
 * @returns An object containing the image URL and loading state
 */
export function useGenerateImage(
  prompt: string,
  model?: string
): {
  url: string | null;
  loading: boolean;
};
